class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])
        nums = []
        ans = -inf
        for i in range(n):
            nn = sorted([(board[i][j], j) for j in range(m)], key=lambda x: -x[0])[:3]
            nums.append((max(board[i]), nn))
        nums.sort(key=lambda x:-x[0])
        mn = min(9,len(nums))
        for ia in range(mn):
            for a,ja in nums[ia][1]:
                for ib in range(mn):
                    if ib == ia:
                        continue
                    for b,jb in nums[ib][1]:
                        if jb == ja:
                            continue
                        for ic in range(mn):
                            if ic == ia or ic == ib:
                                continue
                            for c,jc in nums[ic][1]:
                                if jc == ja or jc == jb:
                                    continue
                                ans = max(ans,a + b + c)
        return ans
