class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        l = []
        n,m = len(board),len(board[0])
        for i in range(n):
            for j in range(m):
                l.append((board[i][j],i,j))
        l.sort(reverse=-1)

        def check(a,x1,y1,b,x2,y2):
            if x1==x2 or y1==y2:
                return False

            return True
        mx = -inf
        for i in range(n*m):
            if l[i][0]*3<=mx:
                break
            for j in range(i+1,n*m):
                if (l[i][0]+l[j][0])*3<mx*2:
                    break
                if not check(*l[i],*l[j]):
                    continue
                for k in range(j+1,n*m):
                    if l[i][0]+l[j][0]+l[k][0]<mx:
                        break
                    if check(*l[i],*l[k]) and check(*l[j],*l[k]):
                        mx = max(mx, l[i][0]+l[j][0]+l[k][0])
        return mx if mx>-99999999999 else -1
