class Solution {
public:
    const long long INF = 1e17;
    const long long inf = 1e9+7;
    long long maximumValueSum(vector<vector<int>>& v) {
        using ll = long long;
        int n = v.size(), m = v[0].size();
        ll ans = -INF;
        vector<pair<int, int>>vec[n];
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                vec[i].push_back({v[i][j], j});
            }
            sort(vec[i].begin(), vec[i].end());
            reverse(vec[i].begin(), vec[i].end());
        }

        for(int i = 0; i < n; i++){
            int xx = vec[i][0].second;
            int yy = vec[i][1].second;
            int zz = vec[i][2].second;
            for(int j = 0; j < m; j++){
                if((j != xx) && (j != yy) && (j != zz))continue;
                vector<array<ll, 3>>col_max;
                for(int p = 0; p < n; p++){
                    if(p == i)continue;
                    int r = 0;
                    for(auto x: vec[p]){
                        if(x.second == j)continue;
                        col_max.push_back({x.first, p, x.second});
                        if(++r == 2)break;
                    }
                }
                auto is_ok = [&](int p, int q){
                    return (col_max[p][1] != col_max[q][1]) && (col_max[p][2] != col_max[q][2]);
                };
                sort(col_max.begin(), col_max.end()); 
                reverse(col_max.begin(), col_max.end()); 
                for(int p = 0; p < col_max.size(); p++){
                    for(int q = p+1; q < col_max.size(); q++){
                        if(is_ok(p, q)){
                            ans = max(ans, 1LL*v[i][j] + col_max[p][0] + col_max[q][0]);
                            break;
                        }
                    }
                    if(p > 1)break;
                }
            }
        }
        return ans;
    }
};
