class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        // brute force
        long long ans = LLONG_MIN / 6;
        int m = board.size();
        int n = board[0].size();
        vector<pair<long long,pair<int,int>>> v;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                v.push_back({board[i][j],{i,j}});
            }
        }
        sort(v.begin(),v.end(),greater<pair<long long,pair<int,int>>>());
        int total = v.size();
        for(int i=0;i<total;i++){
            if(ans >= v[i].first * 3) 
                break;
            for(int j=i+1;j<total;j++){
                if(ans * 2 >= (v[j].first + v[i].first) * 3) 
                    break;
                if(v[i].second.first != v[j].second.first && v[i].second.second != v[j].second.second){
                    for(int k=j+1;k<total;k++){
                        if(v[j].first + v[i].first + v[k].first <= ans) break; 
                        if(v[j].second.first != v[k].second.first &&
                            v[k].second.first != v[i].second.first &&
                            v[j].second.second != v[k].second.second && 
                            v[k].second.second != v[i].second.second)
                        {
                            ans = max(ans,(long long)v[i].first+v[j].first+v[k].first);
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
