class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size(), m = board[0].size();
        vector<vector<int>> temp;
        for( int i=0; i<n; i++ )
        {
            for( int j=0; j<m; j++ )
            {
                temp.push_back({board[i][j],i,j});
            }
        }
        sort( temp.begin(), temp.end(), greater<vector<int>>() );
        int FR, SR, FC, SC, mini = temp.size();
        long long ans = -3000000000;
        for( int i=0; i<n*m; i++ )
        {
            //cout << temp[i][0];
            if( i==mini )break;
            long long t = temp[i][0];
            FR = temp[i][1];
            FC = temp[i][2];
           // cout << temp[i][0];
            for( int j=i+1; j<n*m; j++ )
            {if( j==mini )break;
                long long x = 0;
                if( temp[j][1]!=FR && temp[j][2]!=FC )
                {
                    //cout << temp[j][0];
                    x += temp[j][0];
                    SR = temp[j][1];
                    SC = temp[j][2];
                    int k = j+1;
                    while( k<n*m && (temp[k][1]==FR || temp[k][2]==FC || temp[k][1]==SR || temp[k][2]==SC) )k++;
                    if( k==n*m )continue;
                    x += temp[k][0];
                    ans = max(ans,t+x);
                    //cout << temp[k][0];
                    mini = min(mini,k);
                }
            }
            //cout << "-";

        }
        return ans;
    }
};
