class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& bbb) {

        int n=bbb.size();
        int m=bbb[0].size();
         vector<vector<long long int>> b(n,vector<long long int>(m));
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                b[i][j]=bbb[i][j];
            }
        }
        vector<pair<vector<long long int>,long long int>>f;
        vector<vector<long long int>>row;
        for(int i=0;i<n;i++)
        {
            vector<pair<int,int>>v;
            for(int j=0;j<m;j++)
            {
                v.push_back({b[i][j],j});
            }
            sort(v.begin(),v.end());
            reverse(v.begin(),v.end());
            vector<long long int>temp;
            for(int k=0;k<3;k++)temp.push_back(v[k].second);
            row.push_back(temp);
        }
        vector<vector<long long int>>rrr=row;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<3;j++)
            {
                rrr[i][j]=b[i][row[i][j]];
            }
        }
        for(long long int i=0;i<n;i++)
        {
            f.push_back({rrr[i],i});
        }
        sort(f.begin(),f.end());
        reverse(f.begin(),f.end());
        long long int ans=-1e15;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                // for(int k=0;k<n;k++)
                // {

                    if(i==j)continue;
                for(int u=0;u<3;u++)
                {
                    int yyy=f[u].second;
                    if(yyy==j||yyy==i)continue;
                    int k =yyy;

                    for(int a=0;a<3;a++)
                    {
                        for(int bb=0;bb<3;bb++)
                        {
                            for(int c=0;c<3;c++)
                            {
                                int p = row[i][a];
                                int q = row[j][bb];
                                int r = row[k][c];
                                if(p!=q&&q!=r&&p!=r)
                                {


                                    long long int cnt=b[i][p]+b[j][q]+b[k][r];

                                    ans=max(ans,cnt);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;

    }
};
