class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<pair<long long int, pair<int, int>>> cells;
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                cells.push_back({(long long int)board[i][j], {i, j}});
            }
        }
        sort(cells.begin(), cells.end(), greater<pair<long long int, pair<int, int>>>());
        long long int maxSum = INT64_MIN;
        for (int i = 0; i < cells.size(); ++i) {
            int flag = 0;
            for (int j = i + 1; j < cells.size(); ++j) {
                if (cells[i].second.first != cells[j].second.first &&
                    cells[i].second.second != cells[j].second.second) {
                    for (int k = j + 1; k < cells.size(); ++k) {
                        if (cells[i].second.first != cells[k].second.first &&
                            cells[i].second.second != cells[k].second.second &&
                            cells[j].second.first != cells[k].second.first &&
                            cells[j].second.second != cells[k].second.second) {
                            long long int sum = cells[i].first + cells[j].first + cells[k].first;
                            maxSum = max(maxSum, sum);
                            flag++;
                            break; 
                        }
                    }
                    if(flag >= 5) break;
                }
            }
        }
        return maxSum;
    }
};
