class Solution {
public:
    long long ans;
    void dfs(long long val, int cnt, int idx, vector<pair<int, int> > &cur, vector<vector<int> > &board, vector<pair<int, int> > &isset){
        if(cnt == 3){
            ans = max(ans, val);
            return ;
        }
        for(int i=idx;i<cur.size();i++){
            int ci = cur[i].first, cj = cur[i].second; 
            bool flag = false;
            for(int k=0;k<isset.size();k++){
                if(ci == isset[k].first || cj == isset[k].second){
                    flag = true;
                    break;
                }
            }
            if(!flag){
                isset.push_back(cur[i]);
                dfs(val+board[ci][cj], cnt+1, i+1, cur, board, isset);
                isset.pop_back();
            }
        }
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<pair<int, int> > cur;
        for(int i=0;i<n;i++){
            vector<pair<int, int> > pvec;
            for(int j=0;j<m;j++){
                pvec.push_back({i, j});
            }
            sort(pvec.begin(), pvec.end(), [&board](pair<int, int> &a, pair<int, int> &b){
                    return board[a.first][a.second] > board[b.first][b.second];
                }
            );
            for(int j=0;j<3;j++)
                cur.push_back(pvec[j]);
        }

        vector<pair<int, int> > final_cur;
        for(int i=0;i<m;i++){
            vector<pair<int, int> > pvec;
            for(int j=0;j<n;j++){
                pvec.push_back({j, i});
            }
            sort(pvec.begin(), pvec.end(), [&board](pair<int, int> &a, pair<int, int> &b){
                    return board[a.first][a.second] > board[b.first][b.second];
                }
            );
            for(int j=0;j<3;j++){
                for(auto &p : cur){
                    if(p.first == pvec[j].first && p.second == pvec[j].second)
                        final_cur.push_back(p);
                }
            }
        }

        vector<pair<int, int> > isset;
        ans = LLONG_MIN;
        dfs(0, 0, 0, final_cur, board, isset);
        return ans;
    }
};
