class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = LLONG_MIN;
        vector<vector<pair<long long, int>>> arr;
        vector<pair<long long, pair<int, int>>> joke;
        for (int i = 0; i < board.size(); ++i) {
            for (int j = 0; j < board[i].size(); ++j) {
                joke.push_back({board[i][j], {i, j}});
            }
        }
        sort(joke.begin(), joke.end());
        reverse(joke.begin(), joke.end());
        for (auto x: board) {
            vector<pair<long long, int>> cur;
            for (int i = 0; i < x.size(); ++i) {
                cur.emplace_back(x[i], i);
            }
            sort(cur.begin(), cur.end());
            vector<pair<long long, int>> go;
            go.emplace_back(cur.back()); cur.pop_back();
            go.emplace_back(cur.back()); cur.pop_back();
            go.emplace_back(cur.back());
            arr.emplace_back(go);
        }

        for (int i = 0; i < arr.size(); ++i) {
            for (auto x: arr[i]) {
                for (int j = i + 1; j < arr.size(); ++j) {
                    for (auto y: arr[j]) {
                        if (y.second == x.second) continue;
                        for (auto z: joke) {
                            if (z.second.first != i && z.second.second != x.second) {
                                if (z.second.first != j && z.second.second != y.second) {
                                    ans = max(ans, x.first + y.first + z.first);
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
