class Solution {
public:
    static bool cmp( vector<pair<long long, pair<long, long>>>a,  vector<pair<long long, pair<long, long>>> b)
    {
        return (a[0].first+a[1].first+a[2].first)>(b[0].first+b[1].first+b[2].first);
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<long long, pair<long, long>>>> v(n);
        for (int i = 0; i < n; i++) {
            priority_queue<pair<int, pair<int, int>>,
                           vector<pair<int, pair<int, int>>>,
                           greater<pair<int, pair<int, int>>>>
                pq;
            for (int j = 0; j < m; j++) {
                pq.push({board[i][j], {i, j}});
                if (pq.size() > 3) {
                    pq.pop();
                }
            }
            while (!pq.empty()) {
                auto x = pq.top();
                pq.pop();
                v[i].push_back(x);
            }
        }
        sort(v.begin(),v.end(),cmp);
        long long Size = min(200,n);
        long long ans = -1e12;
        for (int i = 0; i < (Size - 2); i++) {
            for (int j = i + 1; j < (Size - 1); j++) {
                for (int k = j + 1; k < (Size); k++) {
                    for (int p = 0; p < 3; p++) {
                        for (int q = 0; q < 3; q++) {
                            for (int r = 0; r < 3; r++) {
                                int l1 = v[i][p].second.first, r1 = v[i][p].second.second;
                                int l2 = v[j][q].second.first, r2 = v[j][q].second.second;
                                int l3 = v[k][r].second.first, r3 = v[k][r].second.second;
            if (l1 != l2 && l2 != l3 && l1 != l3 &&r1 != r2 && r2 != r3 && r1 != r3) {
                ans = max(ans, (long long)(v[i][p].first +v[j][q].first +v[k][r].first));
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
