class Solution {

    struct point{
int a, b, c;
    };

       struct point2{
int v1, v2, i, j, ii, jj;
    };
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int ar[board.size() + 3][board[0].size() + 3];

        vector<point> mm;
        int n = board.size();
        int m = board[0].size();

        for(int i = 1; i <= n; i++) {

            for(int j = 1; j <= m; j++) {
                ar[i][j] = board[i - 1][j - 1];
               // cout << ar[i][j] << " ";

            }

           // cout << endl;
        }

for(int i = 1; i <= m; i++) {
 vector<pair<int,int>> vv;
    for(int j = 1; j <= n; j++) {
        int v= ar[j][i];
vv.push_back({ar[j][i], j});
// cout << ar[j][i] << " ";
    }
    // cout << endl;


     sort(vv.begin(), vv.end());


      mm.push_back({vv[vv.size() - 1].first, vv[vv.size() - 1].second, i});
     mm.push_back({vv[vv.size() - 2].first, vv[vv.size() - 2].second, i});
     mm.push_back({vv[vv.size() - 3].first, vv[vv.size() - 3].second, i});

}




vector<point2> all; 
for(int i = 0; i <mm.size(); i++) {
    for(int j = i; j < mm.size(); j++) {
        if(mm[i].b != mm[j].b && mm[i].c != mm[j].c)
        all.push_back({mm[i].a, mm[j].a, mm[i].b, mm[i].c, mm[j].b, mm[j].c});
    }
}
long long ans = -1e18;
sort(mm.begin(), mm.end(), [&] (const point& p1, const point& p2) {
    return p1.a > p2.a;
});
for(int l = 0; l < all.size(); l++) {
    for(int f = 0;f < mm.size(); f++) {
        long long c = 1LL * all[l].v1 + 1LL * all[l].v2 + 1LL * mm[f].a;
        point2 z = all[l];
        point x = mm[f];
        if(z.i != x.b && z.ii != x.b && z.j != x.c && z.jj != x.c) {
            ans = max (ans, c);
            break;
        }
    }
}



    return ans;
    }
};
