type oops struct {
    v, c int
}

func maximumValueSum(board [][]int) int64 {
    sorted := make([][3]oops, len(board))
    for y := range sorted {
        sorted[y] = [3]oops{}
        for x := range sorted[y] {
            sorted[y][x].v = -2000000000
        }
        for x, v := range board[y] {
            if sorted[y][0].v <= v {
                sorted[y][0], sorted[y][1], sorted[y][2] = oops{v, x}, sorted[y][0], sorted[y][1]
            } else if sorted[y][1].v <= v {
                sorted[y][1], sorted[y][2] = oops{v, x}, sorted[y][1]
            } else if sorted[y][2].v <= v {
                sorted[y][2] = oops{v, x}
            }
        }
    }

    sort.Slice(sorted, func(i, j int)bool {
        return sorted[i][0].v > sorted[j][0].v         
    })
    hack2 := int64(sorted[0][0].v + sorted[1][0].v)
    hack1 := int64(sorted[0][0].v)

    best := int64(board[0][0]) + int64(board[1][1]) + int64(board[2][2])

    for i := 0; i < 3; i++ {
        for j := 0; j < 3; j++ {
            for k := 0; k < 3; k++ {
                for r1 := 2; r1 < len(sorted); r1++ {
                    a := sorted[r1][i]
                    if int64(a.v) + hack2 <= best {
                        continue
                    }
                    for r2 := 1; r2 < r1; r2++ {
                        b := sorted[r2][j]
                        if a.c == b.c || int64(a.v) + int64(b.v) + hack1 <= best {
                            continue
                        }
                        for r3 := 0; r3 < r2; r3++ {            
                            c := sorted[r3][k]
                            if a.c == b.c || b.c == c.c || c.c == a.c {
                                continue
                            }
                            v := int64(a.v) + int64(b.v) + int64(c.v)
                            if v > best {
                                best = v
                            }
                        }
                    }
                }                
            }
        }
    }
    return best
}
