class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        priority_queue<pair<long long, pair<int, int>>> pq;
        int n = board.size(), m = board[0].size();

        for(int i = 0; i < n; ++i) {
            for(int j = 0; j < m; ++j) {
                pq.push({(long long)board[i][j], {i, j}});
            }
        }

        long long ans = -4e9;
        for(int tt = 0; tt < 4; ++tt) {
            long long cur = pq.top().first;
            int ci = pq.top().second.first;
            int cj = pq.top().second.second;

            priority_queue<pair<long long, pair<int, int>>> qq;
            for(int i = 0; i < n; ++i) {
                if(i == ci) continue;
                for(int j = 0; j < m; ++j) {
                    if(j == cj) continue;
                    qq.push({(long long)board[i][j], {i, j}});
                }
            }

            for(int ttt = 0; ttt < 2; ++ttt) {
                long long cur2 = qq.top().first;
                int ti = qq.top().second.first;
                int tj = qq.top().second.second;
                long long mx = -1e9-5;

                for(int i = 0; i < n; ++i) {
                    if(i == ti || i == ci) continue;
                    for(int j = 0; j < m; ++j) {
                        if(j == tj || j == cj) continue;
                        mx = max(mx, (long long)board[i][j]);
                    }
                }

                long long temp = cur + cur2 + mx;
                ans = max(ans, temp);
                qq.pop();
            }
            pq.pop();
        }

        return ans;
    }
};
