class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();int m=board[0].size();
        vector<vector<int>> ms;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ms.push_back({-board[i][j],i,j});
            }
        }
        long long res=-1e18;
        sort(ms.begin(),ms.end());
        while(ms.size()>(800))ms.pop_back();


        for(int index=0;index<ms.size();index++){
            int i=ms[index][1],j=ms[index][2];    
            long long ans=-1e18;
            for(int k=index+1;k<ms.size();k++){
                long long v=-ms[k][0],r=ms[k][1],c=ms[k][2];
                if(i==r or c==j)continue;
                for(int l=k+1;l<ms.size();l++){
                    long long r1=ms[l][1],c1=ms[l][2];
                    if(r1!=r and c1!=c and i!=r1 and j!=c1){
                        ans=max(ans,v-ms[l][0]);break;
                    }
                }
            }
            res=max(res,board[i][j]+ans);
        }
        return res;

    }
};
