#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size();
        ll m = board[0].size();
        vector<vector<pair<ll,ll>>>pqs(n);
        for (ll i = 0; i<n; i++){
            for (ll j = 0; j<m; j++){
                pqs[i].push_back({board[i][j],j});
            }
        }
        vector<pair<ll,pair<ll,ll>>>just;
        for (ll i = 0; i<n; i++){
            for (ll j = 0; j<m; j++){
                just.push_back({board[i][j],{i,j}});
            }
        }

        for (ll i = 0; i<n; i++){
            sort(pqs[i].begin(),pqs[i].end());
            reverse(pqs[i].begin(),pqs[i].end());
        }

        sort(just.begin(),just.end());
        reverse(just.begin(),just.end());

        ll fans = -1e18;
        for (ll i = 0; i<n; i++){
            for (ll j = 0; j<n; j++){
                if(i != j){
                    ll ans = 0;
                    ans += pqs[i][0].first;
                    int fr = pqs[i][0].second;
                    int a = 0;
                    while (a<m && pqs[j][a].second == fr){
                        a++;
                    }
                    if(a<m){
                        ans += pqs[j][a].first;
                        int sr = pqs[j][a].second;
                        ll b = 0;
                        while (b<n*m){
                            int ni = just[b].second.first;
                            int nj = just[b].second.second;
                            if(ni != i && ni != j && nj != fr && nj != sr){
                                ans += just[b].first;
                                break;
                            }
                            b++;
                        }
                        if(b<n*m){
                            fans = max(ans,fans);
                        }
                    }
                }
            }
        }
        return fans;
    }
};
