#define ll long long 
#define pb push_back

class Solution {
public:
    ll func(vector<vector<int>>& brd, int k, vector<int>& row, vector<int>& col) {
        if (k == 3) return 0;
        ll ans = -1e15;
        for (auto& it : brd) {
            int val = it[0];
            int i = it[1];
            int j = it[2];
            if (row[i] || col[j]) continue;
            row[i] = 1;
            col[j] = 1;
            ans = max(ans, val + func(brd, k + 1, row, col));
            row[i] = 0;
            col[j] = 0;
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int, int>>> nums(n);
        for (int i = 0; i < n; i++) {
            vector<pair<int, int>> v;
            for (int j = 0; j < m; j++) {
                v.pb({board[i][j], j});
            }
            sort(v.begin(), v.end());
            for (int j = 0; j < 3; j++) {
                nums[i].pb(v[m - 1 - j]);
            }
        }
        vector<vector<int>> brd;
        for (int j = 0; j < 3; j++) {
            vector<vector<int>> v;
            for (int i = 0; i < n; i++) {
                v.pb({nums[i][j].first, i, nums[i][j].second});
            }
            sort(v.begin(), v.end());
            for (int i = 0; i < min( 10 , n ); i++) {
                brd.pb(v[n - 1 - i]);
            }
        }
        vector<int> row(n, 0), col(m, 0);
        return func(brd, 0, row, col);
    }
};
