class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        all_vals = []
        n,m = len(board), len(board[0])
        for i in range(n):
            for j in range(m):
                all_vals.append([board[i][j], i, j])
        # all_vals.sort(key = lambda x:-x[0])

        def get_max_val(board, n, m, row_ban, col_ban, all_vals, all_vals_start_idx, cnt):
            # print(row_ban, col_ban)
            max_i, max_j = -1, -1
            max_val = -888888888888888888888888888
            for val,i,j in all_vals:
                if row_ban[i]:
                    continue
                if col_ban[j]:
                    continue
                if val > max_val:
                    max_val = val
                    max_i, max_j = i, j

            if cnt == 1:
                # print(max_val)
                return max_val

            sub_vals = []
            for val, i, j in all_vals:
                if i == max_i and col_ban[j] == False:
                    sub_vals.append([val, i, j])
            sub_vals.sort(key = lambda x:-x[0])

            js = []
            res = -99999999999999
            if len(sub_vals) < cnt:
                # print("-999999999: ", i, cnt, sub_vals)
                return -99999999999999


            row_ban[max_i] = True
            for i in range(cnt):
                js.append(sub_vals[i][2])
                tmp_j = sub_vals[i][2]
                # print(cnt, max_i, tmp_j)

                col_ban[tmp_j] = True

                res = max(res, board[max_i][tmp_j] + get_max_val(board, n, m, row_ban, col_ban, all_vals, all_vals_start_idx, cnt - 1))
                col_ban[tmp_j] = False

            row_ban[max_i] = False

            return res

        return get_max_val(board, n, m, [False for i in range(n)], [False for j in range(m)], all_vals, 0, 3)
