class Solution {
public:
    #define ll long long
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n = board.size(), m = board[0].size(), ans = LLONG_MIN;
        vector<vector<pair<ll,ll>>> rs(n, vector<pair<ll,ll>> (3, make_pair(LLONG_MIN, LLONG_MIN)));
        vector<vector<ll>> mval;
        for(ll i = 0; i < n; i++)
        {
            for(ll j = 0; j < m; j++)
            {
                if(board[i][j] > rs[i][0].first)
                {
                    rs[i][0] = {board[i][j], j};    
                }
                if(rs[i][0].first > rs[i][1].first)
                {
                    swap(rs[i][0], rs[i][1]);
                }
                if(rs[i][1].first > rs[i][2].first)
                {
                    swap(rs[i][1], rs[i][2]);
                }
            }
        }
        for(ll i = 0; i < n; i++)
        {
            for(ll j = 0; j < 3; j++)
            {
                vector<ll> x = {-rs[i][j].first, i, rs[i][j].second};
                mval.push_back(x);
            }
        }
        sort(mval.begin(), mval.end());
        for(ll i = 0; i < n; i++)
        {
            unordered_set<ll> cl;
            for(ll j = 0; j < 3; j++)
            {
                pair<ll,ll> it = rs[i][2-j];
                cl.insert((it).second);
                for(ll k = i+1; k < n; k++)
                {
                    if(k != i)
                    {
                        for(ll l = 0; l < 3; l++)
                        {
                            pair<ll,ll> xt = rs[k][2-l];
                            if(cl.find((xt).second) == cl.end())
                            {
                                cl.insert((xt).second);
                                for(ll p = 0; p < 9; p++)
                                {
                                    if(mval[p][1] != i && mval[p][1] != k && cl.find(mval[p][2]) == cl.end())
                                    {
                                                ll cur = (it).first + (xt).first - mval[p][0];
                                                ans = max(ans, cur);
                                    }
                                }
                                cl.erase((xt).second);
                            }
                        }
                    }
                }
                cl.erase((it).second);
            }
        }
        return ans;
    }
};
