class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        # import random
        # board = [[random.randint(-10**9, 10**9) for i in range(500)] for _ in range(500)]
        # board = [[-1 for i in range(500)] for _ in range(500)]

        m, n = len(board), len(board[0])
        sorted_indices = sorted(list(itertools.product(range(m), range(n))), key=(lambda x: board[x[0]][x[1]]), reverse=True)

        top_3_indices = []
        for row in board:
            top_3_indices.append(sorted(range(n), key=lambda j: row[j])[-3:])

        res = -math.inf
        for i1 in range(m):
            for i2 in range(i1 + 1, m):
                for j1 in top_3_indices[i1]:
                    for j2 in top_3_indices[i2]:
                        if j1 == j2:
                            continue
                        for i3, j3 in sorted_indices:
                            if i3 == i1 or i3 == i2 or j3 == j1 or j3 == j2:
                                continue
                            cur_res = board[i1][j1] + board[i2][j2] + board[i3][j3]
                            res = max(res, cur_res)
                            break
        return res
