class Solution {
    class Point implements Comparable<Point>{
        int x,y;
        Long score;
        public Point(int xx,int yy,int sc){
            score=(long)sc;x=xx;y=yy;
        }

        @Override
        public int compareTo(Point p){
            return score.compareTo(p.score);
        }

        public String toString(){
            return score+" "+x+" "+y;
        }
    }

    boolean crash(Point pt1,Point pt2){
        return pt1.x==pt2.x || pt1.y==pt2.y;
    }

    public long maximumValueSum(int[][] board) {
        int n=board.length;
        int m=board[0].length;
        List<Point> pts=new ArrayList<>();
        for (int i=0;i<n;i++){
            for (int j=0;j<m;j++){
                pts.add(new Point(i,j,board[i][j]));
            }
        }
        Collections.sort(pts);
        long ret=-1000000000;ret*=4;
        for (int i=n*m-1;i>=n*m-4;i--){
            int t=0;int j=i-1;
            while (t<2){
                while (j>=0&&crash(pts.get(i),pts.get(j))){
                    j--;
                }
                t++;
                if (j>=0){
                    int k=j-1;
                    while (k>=0&&(crash(pts.get(i),pts.get(j))|| crash(pts.get(i),pts.get(k))|| crash(pts.get(j),pts.get(k)))){
                        k--;
                    }
                // System.out.println(i+" "+j+" "+k);
                    if (k>=0){
                        long nret=pts.get(i).score+pts.get(j).score+pts.get(k).score;
                        if (nret>ret){
                            ret=nret;
                        }
                    // System.out.println(pts.get(i).toString());
                    //  System.out.println(pts.get(j).toString());
                        //  System.out.println(pts.get(k).toString());
                    }
                    j--;
                }
            }
        }
        return ret;
    }
}
