#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long mx = LLONG_MIN;
        vector<vector<int>> arr(m, vector<int>(n, 0));
        if(n==3){
            for(int i=0;i<m;i++){
            iota(arr[i].begin(),arr[i].end(),0);
        }
        int z=0;
        auto compare=[&](int i,int j){
            return board[z][i]>board[z][j];
        };
        for(int i=0;i<m;i++){
            sort(arr[i].begin(),arr[i].end(),compare);
            z++;
        }
        for(int i=0;i<m-2;i++){
            for(int j=i+1;j<m-1;j++){
                for(int k=j+1;k<m;k++){
                    for(int x=0;x<3;x++){
                        for(int y=0;y<3;y++){
                            for(int z=0;z<3;z++){
                                if(arr[i][x]!=arr[j][y] and arr[j][y]!=arr[k][z] and arr[k][z]!=arr[i][x]){

                                    mx=max(mx,(ll)board[i][arr[i][x]]+(ll)board[j][arr[j][y]]+(ll)board[k][arr[k][z]]);










                                }
                            }
                        }
                    }                    





                }
            }
        }
        return mx;
        }
        else{
        for (int i = 0; i < m; i++) {
            iota(arr[i].begin(), arr[i].end(), 0);
        }
        int z = 0;
        auto compare = [&](int i, int j) { return board[z][i] > board[z][j]; };
        for (int i = 0; i < m; i++) {
            sort(arr[i].begin(), arr[i].end(), compare);
            z++;
        }
        for (int i = 0; i < m - 1; i++) {
            for (int j = i + 1; j < m; j++) {
                if (board[i][arr[i][0]] == board[j][arr[j][0]] and
                    board[i][arr[i][1]] == board[j][arr[j][1]] and
                    board[i][arr[i][2]] < board[j][arr[j][2]]) {
                    swap(arr[i], arr[j]);
                    swap(board[i],board[j]);
                } else if (board[i][arr[i][0]] == board[j][arr[j][0]] and
                           board[i][arr[i][1]] < board[j][arr[j][1]]) {
                    swap(arr[i], arr[j]);
                    swap(board[i],board[j]);
                } else if (board[i][arr[i][0]] < board[j][arr[j][0]]) {
                    swap(arr[i], arr[j]);
                    swap(board[i],board[j]);
                }
            }
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                cout<<board[i][arr[i][j]]<<" ";
            }
            cout<<"\n";
        }
        ll mx1=LLONG_MIN;
        for (int i = 0; i < min(4,m); i++) {
            for (int j = i + 1; j < min(5,m); j++) {
                for (int k = j + 1; k < min(6,m); k++) {
                    for (int x = 0; x < 3; x++) {
                        for (int y = 0; y < 3; y++) {
                            for (int z = 0; z < 3; z++) {
                                if (arr[i][x] != arr[j][y] and
                                    arr[j][y] != arr[k][z] and
                                    arr[k][z] != arr[i][x]) {
                                    mx1=mx;
                                    mx = max(mx, (ll)board[i][arr[i][x]] +
                                                     (ll)board[j][arr[j][y]] +
                                                     (ll)board[k][arr[k][z]]);
                                    if(mx1!=mx){
                                        cout<<board[i][arr[i][x]]<<" "<<
                                                     (ll)board[j][arr[j][y]] <<" "<<
                                                     (ll)board[k][arr[k][z]]<<"\n";
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
        return mx;
        }
    }
};
