class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m,n = len(board),len(board[0])
        ls = []
        mn = min([min(a) for a in board])
        #print(mn)
        for i in range(m):
            for j in range(n):
                ls.append((board[i][j]-mn,i,j))
        ls.sort(reverse= True)
        #print(ls)
        ret =[]
        def verify(ls):
            #print(ls)
            re = ls[0][0] + ls[1][0] + ls[2][0]
            als = set([ls[0][1],ls[1][1],ls[2][1]])
            bls = set([ls[0][2],ls[1][2],ls[2][2]])
            if len(als) ==3 and len(bls)==3:
                return re
            return -1
        mx =-10**10
        for k,i,j in ls:
            tmp=[]

            for cnd in ret:
                tmp.append(list(cnd))
                if len(cnd) ==1:
                    if cnd[0][1] != i and cnd[0][2] !=j:
                        cnd.append((k,i,j))
                        tmp.append(cnd)
                elif len(cnd) ==2:
                    cnd.append((k,i,j))
                    if len(cnd) == 3:
                        if verify(cnd)!=-1:
                            mx = max(mx,verify(cnd))
            tmp.append([(k,i,j)])

            tmp.sort(key=lambda ls: sum(a[0] for a in ls), reverse=True)
            ret = tmp[:9]
            #print(ret)
        return mx +mn*3
