class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<pair<int, pair<int, int>>> sqs;
        if (n < m) {
            for (int i = 0; i < n; ++i) {
                pair<int, int> m1 = {INT_MIN, -1}, m2 = {INT_MIN, -1}, m3 = {INT_MIN, -1};
                for (int j = 0; j < m; ++j) {
                    if (board[i][j] > m1.first) {
                        m3 = m2;
                        m2 = m1;
                        m1 = {board[i][j], j};
                    } else if (board[i][j] > m2.first) {
                        m3 = m2;
                        m2 = {board[i][j], j};
                    } else if (board[i][j] > m3.first) {
                        m3 = {board[i][j], j};
                    }
                }
                sqs.push_back({m1.first, {i, m1.second}});
                sqs.push_back({m2.first, {i, m2.second}});
                sqs.push_back({m3.first, {i, m3.second}});
            }
        } else {
            for (int j = 0; j < m; ++j) {
                pair<int, int> m1 = {INT_MIN, -1}, m2 = {INT_MIN, -1}, m3 = {INT_MIN, -1};
                for (int i = 0; i < n; ++i) {
                    if (board[i][j] > m1.first) {
                        m3 = m2;
                        m2 = m1;
                        m1 = {board[i][j], i};
                    } else if (board[i][j] > m2.first) {
                        m3 = m2;
                        m2 = {board[i][j], i};
                    } else if (board[i][j] > m3.first) {
                        m3 = {board[i][j], i};
                    }
                }
                sqs.push_back({m1.first, {m1.second, j}});
                sqs.push_back({m2.first, {m2.second, j}});
                sqs.push_back({m3.first, {m3.second, j}});
            }
        }

        int o = sqs.size();
        long long ans = LLONG_MIN;
        int ind = 0;
        for (int i = 0; i < o; ++i) {
            for (int j = i + 1; j < o; ++j) {
                if (sqs[i].second.first == sqs[j].second.first ||sqs[i].second.second == sqs[j].second.second) continue;
                for (int k = j + 1; k < o; ++k) {
                    if (
                        sqs[i].second.first == sqs[k].second.first ||
                        sqs[i].second.second == sqs[k].second.second ||
                        sqs[j].second.first == sqs[k].second.first ||
                        sqs[j].second.second == sqs[k].second.second
                    ) continue;

                    // cout << sqs[i].second.first << " " << sqs[i].second.second << endl;
                    // cout << sqs[j].second.first << " " << sqs[j].second.second << endl;
                    // cout << sqs[k].second.first << " " << sqs[k].second.second << endl;
                    // cout << sqs[i].first << " " << sqs[j].first << " " << sqs[k].first << endl;
                    long long crnt = 0ll + sqs[i].first + sqs[j].first + sqs[k].first;
                    // cout << crnt << endl;
                    ans = max(ans, crnt);
                }
            }
        }
        return ans;
    }
};
