class Solution {
public:

    int ml[600][600],mr[600][600];
    int a[3][600],mxl[3][600],mxr[3][600];
    long long ans, tmpA, tmpB, tmpC;

    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();

        for (int i=0;i<n;i++) {
            for (int j=0;j<m;j++) {
                ml[i][j]=board[i][j];
                if (j>0) ml[i][j]=max(ml[i][j],ml[i][j-1]);
            }
            for (int j=m-1;j>=0;j--) {
                mr[i][j]=board[i][j];
                if (j<(m-1)) mr[i][j]=max(mr[i][j],mr[i][j+1]);
            }
        }

        ans = -1000LL*1000LL*1000LL*3LL;
        for (int j=1;j<m-1;j++) {

            for (int i=0;i<n;i++) {
                a[0][i]=board[i][j];
                a[1][i]=ml[i][j-1];
                a[2][i]=mr[i][j+1];
            }

            for (int k=0;k<3;k++) {
                for (int i=0;i<n;i++) {
                    mxl[k][i]=a[k][i];
                    if (i>0) mxl[k][i]=max(mxl[k][i],mxl[k][i-1]);
                }
                for (int i=n-1;i>=0;i--) {
                    mxr[k][i]=a[k][i];
                    if (i<(n-1)) mxr[k][i]=max(mxr[k][i],mxr[k][i+1]);
                }
            }

            for (int i=1;i<n-1;i++)
                for (int aa=0;aa<3;aa++)
                    for (int bb=0;bb<3;bb++) if (aa!=bb)
                        for (int cc=0;cc<3;cc++) if (aa!=cc && bb!=cc) {
                            tmpA=mxl[aa][i-1];
                            tmpB=a[bb][i];
                            tmpC=mxr[cc][i+1];
                            ans=max(ans,tmpA+tmpB+tmpC);
                        }
        }

        return ans;
    }
};
