class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n=len(board)
        val=[]
        for idx,row in enumerate(board):
            for i,x in enumerate(row):
                row[i]=(x,i,idx)
            val.extend(sorted(row,reverse=True)[:3])
        val.sort(reverse=True)
        m=len(val)
        adj=[0 for _ in range(m)]
        for i in range(m-1):
            for j in range(i+1,m):
                if val[i][1]!=val[j][1] and val[i][2]!=val[j][2]:
                    adj[i]|=1<<j
                    adj[j]|=1<<i
        res=-inf

        def find(n):
            res=0
            while not n&((1<<64)-1):
                n>>=64
                res+=64
            if not n&((1<<32)-1):
                n>>=32
                res+=32
            if not n&((1<<16)-1):
                n>>=16
                res+=16
            if not n&((1<<8)-1):
                n>>=8
                res+=8
            if not n&((1<<4)-1):
                n>>=4
                res+=4
            if not n&((1<<2)-1):
                n>>=2
                res+=2
            if not n&((1<<1)-1):
                n>>=1
                res+=1
            return res

        for i in range(m-1):
            for j in range(i+1,m):
                if not adj[i]&(1<<j):
                    continue
                s=adj[i]&adj[j]
                if s:
                    idx=find(s)
                    res=max(res,val[i][0]+val[j][0]+val[idx][0])
        return res
