class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m , n = len(board), len(board[0]) 

        arr= []
        for i in range(m):
            for j in range(n):
                arr.append((board[i][j], i, j))

        arr.sort(reverse= True)

        def recur(i, count, rows, cols, rejected):
            if count == 0:
                return 0
            if i== len(arr) or rejected>40:
                return float("-inf")

            val, x, y = arr[i]
            if x in rows or y in cols:
                return recur(i+1, count, rows, cols, rejected)

            choose = val + recur(i+1, count-1, rows+[x], cols+ [y], rejected )

            if count==1:
                ans = choose
            else:
                ans = max(choose, recur(i+1, count, rows, cols, rejected+1))

            return ans

        return recur(0, 3, [], [], 0)
