class Solution {
public:
    using ll = long long;
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<vector<pair<int, int>>> top_values(m);
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                top_values[i].push_back({board[i][j], j});
            }
            sort(top_values[i].rbegin(), top_values[i].rend());
            if (top_values[i].size() > 3) top_values[i].resize(3);
        }

        vector<pair<int, int>> top_rows;
        for (int i = 0; i < m; ++i) {
            top_rows.push_back({top_values[i][0].first, i});
        }
        sort(top_rows.rbegin(), top_rows.rend());
        if (top_rows.size() > 100) top_rows.resize(100);

        ll max_sum = LLONG_MIN;

        for (int ii = 0; ii < top_rows.size(); ++ii) {
            int i = top_rows[ii].second;
            for (int jj = ii + 1; jj < top_rows.size(); ++jj) {
                int j = top_rows[jj].second;
                for (int kk = jj + 1; kk < top_rows.size(); ++kk) {
                    int k = top_rows[kk].second;
                    for (auto& vi : top_values[i]) {
                        for (auto& vj : top_values[j]) {
                            if (vj.second == vi.second) continue;
                            for (auto& vk : top_values[k]) {
                                if (vk.second == vi.second || vk.second == vj.second) continue;
                                ll sum = (ll)vi.first + vj.first + vk.first;
                                max_sum = max(max_sum, sum);
                            }
                        }
                    }
                }
            }
        }

        return max_sum;
    }
};
