class Solution {

public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        long long ans = -1e14;
        pair<long long,long long> mx[n+10][3];
        for(int i = 1;i<=n;i++){
            vector<pair<int,int>> lol;
            for(int j= 0;j<m;j++){
                lol.push_back(make_pair(board[i-1][j],j));
            }
            sort(lol.begin(),lol.end());
            for(int j = 0;j<3;j++){mx[i][j] = lol.back();
            lol.pop_back();}
        }
        for(int i = 1;i<=n;i++){
            for(int j = 1;j<=n;j++){
                for(int k = 1;k<=n;k++){
                    if(!(i != k && i != j && j != k)) continue;
                    // for(int i2 = 0;i2<3;i2++)
                    pair<long long, long long> solu = mx[i][0];
                    long long curr = mx[i][0].first;
                    int j2;
                    for(j2 = 0;j2<3;j2++){
                        if(mx[j][j2].second != solu.second) break;
                    }
                    curr += mx[j][j2].first;
                    int k2;
                    for(k2 = 0;k2<3;k2++){
                        if(mx[k][k2].second != solu.second && mx[k][k2].second != mx[j][j2].second) break;
                    }
                    curr += mx[k][k2].first;
                    ans =max(ans, curr);
                }
            }
        }
        return ans;
    }
};
