class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        long long ans=LLONG_MIN;
        int n=b.size(), m=b[0].size();
        vector<vector<int>> v, arr;
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<m; j++)
            {
                v.push_back({b[i][j], i, j});
            }
        }
        sort(v.rbegin(), v.rend());

        for(int i=0; i<min((int)v.size(), 1820); i++)
            arr.push_back(v[i]);


        for(int i=0; i<arr.size()-2; i++)
        {
            for(int j=i+1; j<arr.size()-1; j++)
            {
                if(arr[i][1]==arr[j][1] || arr[i][2]==arr[j][2])
                    continue;

                for(int k=j+1; k<arr.size(); k++)
                {
                    if(arr[i][1]==arr[k][1] || arr[i][2]==arr[k][2] || arr[j][1]==arr[k][1] || arr[j][2]==arr[k][2])
                        continue;
                    ans=max(ans, (long long)arr[i][0]+arr[j][0]+arr[k][0]);
                    // ok=false;
                    break;
                }
            }
        }

        return ans;
    }
};
