#define pi pair<int, int>
#define pii pair<int, pi>
#define f first
#define s second

const int k = 3;

class Solution {
private:
    void add(vector<pii> &pos, vector<pii> v){
        partial_sort(v.begin(), v.begin() + k, v.end(), greater<pii>());
        pos.insert(pos.end(), v.begin(), v.begin() + k);
    }

public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();

        vector<pii> possy[2];
        for(int i = 0; i < n; i++){
            vector<pii> v;
            for(int j = 0; j < m; j++) v.push_back({board[i][j], {i, j}});
            add(possy[0], v);
        } 
        for(int j = 0; j < m; j++){
            vector<pii> v;
            for(int i = 0; i < n; i++) v.push_back({board[i][j], {i, j}});
            add(possy[1], v);
        }

        vector<pii> pos;
        for(int i = 0; i < 2; i++){
            int x = min((int)possy[i].size(), 2 * k * k);
            sort(possy[i].begin(), possy[i].end(), greater<pii>());
            pos.insert(pos.end(), possy[i].begin(), possy[i].begin() + x);
        }

        long long ret = -4e9;
        vector<bool> hitx(n), hity(m);
        for(int i[k] = {0}; i[0] < pos.size(); i[0]++)
        for(i[1] = 0; i[1] < i[0]; i[1]++)
        for(i[2] = 0; i[2] < i[1]; i[2]++){
            vector<pi> cur;
            for(int j = 0; j < k; j++) cur.push_back(pos[i[j]].s);

            bool good = 1;
            long long sum = 0;
            for(pi x : cur){
                good &= !hitx[x.f] && !hity[x.s];
                sum += board[x.f][x.s];
                hitx[x.f] = hity[x.s] = 1;
            }

            if(good) ret = max(ret, sum);

            for(pi x : cur) hitx[x.f] = hity[x.s] = 0;
        }

        return ret;
    }
};
