class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        res = -10**10
        m, n = len(board), len(board[0])
        arr = []

        for i in range(m):
            comb = list(zip(board[i], list(range(n))))
            comb.sort(reverse = True)
            for j in range(3):
                mv, mp = comb[j]
                arr.append((mv, i, mp))

        arr.sort(reverse = True)        
        leng = len(arr)

        for i in range(min(leng, 12)):
            cv, ci, cj = arr[i]
            for j in range(i + 1, leng):
                nv,ni,nj = arr[j]
                if ni == ci or nj == cj:
                    continue
                for k in range (j + 1, leng):
                    nv2,ni2,nj2 = arr[k]
                    if ni2 == ci or nj2 == cj or ni2 == ni or nj2 == nj:
                        continue
                    res = max(res, cv + nv + nv2)
                    break

        return res
