class Solution {
public:

    long long Cal(vector<vector<int>>& board){
        int n=board.size();
        int m=board[0].size();
        int x=0,y=0;
        for(int i=0;i<n;++i)
            for(int j=0;j<m;++j)
                if(board[i][j]>board[x][y]){
                    x=i;
                    y=j;
                }
        long long f1=board[x][y],f2=-1e9;
        long long f3=-1e9,f4=-1e9;
        for(int i=0;i<n;++i)
            for(int j=0;j<m;++j)
            {
                if(i!=x&&j!=y){
                    f2=max(f2,1ll*board[i][j]);
                }
                if(i==x&&j!=y){
                    f3=max(f3,1ll*board[i][j]);
                }
                if(i!=x&&j==y){
                    f4=max(f4,1ll*board[i][j]);
                }
            }
        return max(f1+f2,f3+f4);
    }

    long long Sol(int x,int y,vector<vector<int>>& board){
        long long ans=board[x][y];
        int n=board.size();
        int m=board[0].size();

        vector<vector<int>>g;
        for(int i=0;i<n;++i)
        if(i!=x)
        {
            g.emplace_back();
            for(int j=0;j<m;++j)
            if(j!=y){
                g.back().push_back(board[i][j]);
            }
        }
        return ans+Cal(g);
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        int x2=0,y1=0;
        for(int i=0;i<n;++i)
            for(int j=0;j<m;++j)
                if(board[i][j]>board[x2][y1]){
                    x2=i;
                    y1=j;
                }

        long long ans=Sol(x2,y1,board);

        int x1=-1,y2=-1;
        for(int y=0;y<m;++y)
            if(y!=y1&&(y2==-1||board[x2][y]>board[x2][y2])){
                y2=y;
            }

        for(int x=0;x<n;++x)
            if(x!=x2&&(x1==-1||board[x][y1]>board[x1][y1])){
                x1=x;
            }
        ans=max(ans,Sol(x1,y1,board));
        ans=max(ans,Sol(x2,y2,board));
        ans=max(ans,Sol(x1,y2,board));

        return ans;
    }
};
