class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        ordered = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                ordered.append(tuple([board[i][j], i, j]))
        ordered.sort(reverse = True)
        max_seen = [float("-inf"), float("-inf"), float("-inf")]
        for first in ordered:
            if sum(max_seen) >= first[0] * 3:
                break
            for second in ordered:
                if second[0] > first[0]:
                    continue
                if sum(max_seen) - first[0] >= second[0] * 2:
                    break
                if second[1] == first[1] \
                    or second[2] == first[2]:
                    continue
                for third in ordered:
                    if third[0] > second[0]:
                        continue
                    if sum(max_seen) - first[0] - second[0] >= third[0]:
                        break
                    if third[1] == first[1] \
                        or third[1] == second[1] \
                        or third[2] == first[2] \
                        or third[2] == second[2]:
                        continue
                    candidate_sum = first[0] + second[0] + third[0]
                    if sum(max_seen) < candidate_sum:
                        max_seen = sorted([first[0], second[0], third[0]])
        return sum(max_seen)
