class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        ref = defaultdict(list)
        # lis = set()
        # for i in range(m):
        #     for j in range(n):
        #         lis.add(board[i][j])
        # lis = list(lis)
        # lis.sort(reverse = True)
        # lis = set(lis[:100])
        for i in range(m):
            for j in range(n):
                v = board[i][j]
                if len(ref[v])<=300:
                    ref[v].append([i,j])
        reserve = defaultdict(list)
        sortedKey = list(sorted(ref.keys(),reverse = True))
        # print(f'sorted:{sortedKey}')
        maxR = -math.inf
        for i,num in enumerate(sortedKey):
            if i>=30:
                break
            # newR = reserve.copy()
            for x,y in ref[num]:
                # if 1 not in reserve:
                reserve[1].append([[x,y]])
                # else:
                for k in range(1,3):
                    if k in reserve:
                        for pairs in reserve[k]:
                            isOk = 1
                            newPairs = [[x,y]]
                            for pair in pairs:
                                x1,y1 = pair
                                if x1!=x and y1!=y:
                                    newPairs.append([x1,y1])
                                else:
                                    isOk=0
                                    break
                            if isOk:
                                reserve[k+1].append(newPairs)
                                if k+1==3:
                                    res = 0
                                    for x2,y2 in newPairs:
                                        res+=board[x2][y2]
                                    maxR = max(maxR,res)



        return maxR
