class Solution:
    def maximumValueSum(self, b: List[List[int]]) -> int:
        n,m=len(b), len(b[0])
        bb = []
        for i in range(n):
            bb.append([])
            for j in range(m):
                bb[i].append([b[i][j],j])
            bb[i].sort(reverse=True)
            bb[i] = bb[i][:3]
        bb2 = []
        for i in range(n-1,-1,-1):
            if i!=n-1:
                for j in range(m):
                    b[i][j] = max(b[i][j], b[i+1][j])
            tmp = [[b[i][j],j] for j in range(m)]
            tmp.sort(reverse=True)
            bb2.append(tmp[:3])
        bb2 = bb2[::-1]

        ans = -float('inf')
        for i in range(n):
            for j in range(i+1,n-1):
                k = j+1
                for ii in range(3):
                    for jj in range(3):
                        if bb[i][ii][1]== bb[j][jj][1]: continue
                        for kk in range(3):
                            if bb[i][ii][1]== bb2[k][kk][1] or bb2[k][kk][1]== bb[j][jj][1]: continue
                            ans = max(ans,bb[i][ii][0]+bb[j][jj][0]+bb2[k][kk][0])
        return ans
