class Solution {
public:
    typedef long long ll;
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n=board.size();
        ll m=board[0].size();
        ll ans=LLONG_MIN;
      vector<tuple<ll,ll,ll>>v;
        for(ll i=0;i<n;i++){
            for(ll j=0;j<m;j++){
                  v.push_back({board[i][j],i,j});
            }

        }
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        vector<ll>row(n,0),col(m,0);
        for(ll i=0;i<n;i++){
            for(ll j=0;j<m;j++){
                for(ll k=0;k<n;k++){
                    row[k]=0;

                }
                 for(ll k=0;k<m;k++){
                    col[k]=0;

                }
               ll val=board[i][j];
                row[i]=1;
                col[j]=1;
                ll cur=0;
            for(auto c:v){
                ll x=get<0>(c);
                ll y=get<1>(c);
                ll z=get<2>(c);
                if(row[y]==0&&col[z]==0){
                       val+=x;
                    cur++;
                    row[y]++;
                    col[z]++;
                    if(cur==2){
                      break;
                    }
                }

            }
                 for(ll k=0;k<n;k++){
                    row[k]=0;

                 }
                 for(ll k=0;k<m;k++){
                    col[k]=0;

                 }
                ll vv=board[i][j],ew=0;
                cur=0;
                row[i]=1;
                col[j]=1;
                if(m>=4&&n>=4){
                 for(auto c:v){
                ll x=get<0>(c);
                ll y=get<1>(c);
                ll z=get<2>(c);

                if(row[y]==0&&col[z]==0){
                    if(cur==0&&ew==0){
                        ew=1;

                    }
                    else{
                    vv+=x;

                    cur++;
                    row[y]++;
                    col[z]++;

                    if(cur==2){
                      break;
                    }


                    }
                }

            }
                }


            ans=max(ans,val);
                if(m>=4&&n>=4){
                ans=max(ans,vv);}



            }

        }
        return ans;

    }
};
