class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])
        row = [[] for _ in range(n)]
        for i in range(n):
            for j in range(m):
                row[i].append(j)
                row[i].sort(key=lambda x: board[i][x], reverse=True)
                if len(row[i]) > 3:
                    row[i] = row[i][:3]
        ans = -math.inf
        index = [i for i in range(n)]
        index.sort(key=lambda x: max(board[x][row[x][0]], board[x][row[x][1]], board[x][row[x][2]]), reverse=True)
        index = index[:100]
        for i in range(len(index)):
            for j in range(i + 1, len(index)):
                for k in range(j + 1, len(index)):
                    x = index[i]
                    y = index[j]
                    z = index[k]
                    for a in row[x]:
                        for b in row[y]:
                            for c in row[z]:
                                if a != b and a != c and b != c:
                                    ans = max(ans, board[x][a] + board[y][b] + board[z][c])
        return ans
