class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m,n=len(board),len(board[0])
        s=tuple(sorted([(board[i][j],i,j) for i in range(m) for j in range(n)],reverse=True))
        @cache
        def check(t,k):
            r=-99999999999
            if k==1:return t[0][0] if t else r
            x=0
            while x+k-1<len(t) and r<sum(t[x+i][0] for i in range(k)):
                r=max(r,t[x][0]+check(tuple(t[i] for i in range(x+1,len(t)) if t[i][1]!=t[x][1] and t[i][2]!=t[x][2]),k-1))
                x+=1
            #print(t,k,r)
            return r
        return check(s,3)
