class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        long long dp[505][505][5];
        long long res=-1e12;
        vector<pair<int,pair<int,int>>> d(n*m);
        int t=0;
        for(int i=0;i<n;++i)
        {
            for(int j=0;j<m;++j)
            {
                d[t++]={-board[i][j],{i,j}};
            }
        }
        sort(d.begin(),d.end());
        long long w1,w2;
        int x1,x2,y1,y2;
        vector<pair<long long,pair<int,int>>> dd(10,{-1e12,{-1,-1}});
        map<int,int> mp;
        int nn=0;
        for(int i=0;i<t;++i)
        {
            w1=d[i].first;
            x1=d[i].second.first;
            y1=d[i].second.second;
            if(mp[x1]>=3){
                continue;
            }
            mp[x1]++;
            dd[nn++]={w1,{x1,y1}};
            if(nn>=10){
                break;
            }
        }
        // for(int i=0;i<10;++i)
        //     cout<<dd[i].first<<" "<<dd[i].second.first<<","<<dd[i].second.second<<endl;
        t=n*3;
        for(int i=0;i<t;++i)
        {
            w1=d[i].first;
            x1=d[i].second.first;
            y1=d[i].second.second;
            for(int j=0;j<t;++j)
            {
                w2=d[j].first;
                x2=d[j].second.first;
                y2=d[j].second.second;
                if(x1==x2||y1==y2){
                    continue;
                }
                for(int k=0;k<10;++k)
                {
                    if(dd[k].second.first!=x1&&dd[k].second.first!=x2&&\
                    dd[k].second.second!=y1&&dd[k].second.second!=y2){
                        res=max(res,-dd[k].first-w1-w2);
                        break;
                    }
                }
            }
        }

        return res;
    }
};
