class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:  
        Mi,Mj,Mv=-1,-1,-float('inf')
        top3=[]        
        for i in range(len(board)):
            tmp=sorted(list(zip(range(len(board[0])),board[i])),key=lambda x:-x[1])[:3]
            if Mv<tmp[0][1]:
                Mi,Mj=i,tmp[0][0]
                Mv=tmp[0][1]
            top3.append(tmp)
        # print(top3,Mi,Mj,Mv)
        def f(i,j,k):
            res=-float('inf')
            for i1,v1 in top3[i]:
                for i2,v2 in top3[j]:
                    for i3,v3 in top3[k]:
                        if i1!=i2 and i1!=i3 and i2!=i3:
                            res=max(res,v1+v2+v3)
            return res 

        out=-float('inf')
        for j in range(len(board)-1):
            if j==Mi:
                continue
            for k in range(j+1,len(board)):
                if k==Mi:
                    continue
                out=max(out,f(Mi,j,k))
        return out
