class Solution {
public:
using LL = long long;
    using Pair = std::pair<int, int>;
    using PQ = std::priority_queue<Pair>;

    LL maximumValueSum(vector<vector<int>>& board) {
        const int m = board.size(), n = board[0].size();

        std::vector<PQ> _pq(m, std::priority_queue<pair<int, int>>());
        std::vector<std::vector<Pair>> candidates(m, std::vector<Pair>(3));
        std::vector<int> acquired(n, 0);
        int r, c;
        int i, j, k;
        int x, y, z;


        // O(mnlogn)
        for (r = 0; r < m; ++r) {
            // O(nlogn)
            for (c = 0; c < n; ++c) {
                _pq[r].emplace(board[r][c], c);
            }

            for (i = 0; i < 3; ++i) {
                candidates[r][i] = _pq[r].top();
                _pq[r].pop();
            }
        }

        sort(candidates.begin(), candidates.end(), [](const std::vector<Pair>& a, const std::vector<Pair>& b) {
            return a[0].first > b[0].first;
        });

        // O(m^3)
        LL result = LLONG_MIN;
        LL sum = LLONG_MIN;
        for (i = 0; i < m; ++i) {
            for (j = i + 1; j < m; ++j) {
                for (x = 0; x < 3; ++x) {
                    for (y = 0; y < 3; ++y) {
                        if (candidates[i][x].second == candidates[j][y].second)
                            continue;

                        for (k = j + 1; k < m; ++k) {
                            for (z = 0; z < 3; ++z) {
                                if (candidates[j][y].second != candidates[k][z].second &&
                                    candidates[k][z].second != candidates[i][x].second)
                                    break;
                            }
                            sum = (LL)candidates[i][x].first + candidates[j][y].first + candidates[k][z].first;
                            result = std::max(result, sum);
                        }
                    }
                }
            }
        }

        return result;
    }
};
