class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<int>>nums;
        for(int i=0;i<board.size();i++){
            for(int j = 0;j<board[0].size();j++){
                nums.push_back({board[i][j],i,j});
            }
        }
        sort(nums.begin(),nums.end());
        long long ans = LLONG_MIN;
        int n = nums.size();
        for(int i = 0;i<min(350UL,nums.size());i++){
            for(int j = i+1;j<min(350UL,nums.size());j++){
                for(int k = j+1;k<min(350UL,nums.size());k++){
                    if(nums[n-1-i][2]!=nums[n-1-j][2]&&nums[n-1-k][2]!=nums[n-1-j][2]&&nums[n-1-i][2]!=nums[n-1-k][2]&&nums[n-1-i][1]!=nums[n-1-j][1]&&nums[n-1-k][1]!=nums[n-1-j][1]&&nums[n-1-i][1]!=nums[n-1-k][1]){
                        ans = max(ans,0LL+nums[n-1-i][0]+nums[n-1-j][0]+nums[n-1-k][0]);
                    }
                }
            }
        }
        return ans;
    }
};
