class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])

        def copy():
            return [[board[i][j] for j in range(n)] for i in range(m)]

        ul, ur, dl, dr = copy(), copy(), copy(), copy()
        ll, rr, uu, dd = copy(), copy(), copy(), copy()

        for i in range(m):
            for j in range(n):
                if i != 0:
                    ul[i][j] = max(ul[i - 1][j], ul[i][j])
                if j != 0:
                    ul[i][j] = max(ul[i][j - 1], ul[i][j])

        for i in range(m - 1, -1, -1):
            for j in range(n):
                if i != m - 1:
                    dl[i][j] = max(dl[i + 1][j], dl[i][j])
                if j != 0:
                    dl[i][j] = max(dl[i][j - 1], dl[i][j])

        for i in range(m):
            for j in range(n - 1, -1, -1):
                if i != 0:
                    ur[i][j] = max(ur[i - 1][j], ur[i][j])
                if j != n - 1:
                    ur[i][j] = max(ur[i][j + 1], ur[i][j])

        for i in range(m - 1, -1, -1):
            for j in range(n - 1, -1, -1):
                if i != m - 1:
                    dr[i][j] = max(dr[i + 1][j], dr[i][j])
                if j != n - 1:
                    dr[i][j] = max(dr[i][j + 1], dr[i][j])

        for i in range(m):
            for j in range(n):
                if i != 0:
                    uu[i][j] = max(uu[i - 1][j], uu[i][j])
                if j != 0:
                    ll[i][j] = max(ll[i][j - 1], ll[i][j])

        for i in range(m - 1, -1, -1):
            for j in range(n - 1, -1, -1):
                if i != m - 1:
                    dd[i][j] = max(dd[i + 1][j], dd[i][j])
                if j != n - 1:
                    rr[i][j] = max(rr[i][j + 1], rr[i][j])

        answer = -10**10 * 3

        for i in range(1, m - 1):
            for j in range(1, n - 1):
                answer = max(answer, board[i][j] + ul[i - 1][j - 1] + dr[i + 1][j + 1])
                answer = max(answer, board[i][j] + ur[i - 1][j + 1] + dl[i + 1][j - 1])
                answer = max(answer, ul[i - 1][j - 1] + rr[i][j + 1] + dd[i + 1][j])
                answer = max(answer, ur[i - 1][j + 1] + ll[i][j - 1] + dd[i + 1][j])
                answer = max(answer, dl[i + 1][j - 1] + rr[i][j + 1] + uu[i - 1][j])
                answer = max(answer, dr[i + 1][j + 1] + ll[i][j - 1] + uu[i - 1][j])

        return answer
