public class Solution {
    public long MaximumValueSum(int[][] board) {
        int[,] dp = new int[board.Length, 3];
        List<(int, int)> list = new List<(int, int)>(board.Length * board[0].Length);

        for (int i = 0; i < board.Length; i++){
            int maxInd1 = -1;
            int maxInd2 = -1;
            int maxInd3 = -1;

            for (int j = 0; j < board[i].Length; j++){
                if (maxInd1 == -1 || board[i][j] > board[i][maxInd1]){
                    maxInd3 = maxInd2;
                    maxInd2 = maxInd1;
                    maxInd1 = j;
                }
                else if (maxInd2 == -1 || board[i][j] > board[i][maxInd2]){
                    maxInd3 = maxInd2;
                    maxInd2 = j;
                }
                else if (maxInd3 == -1 || board[i][j] > board[i][maxInd3]){
                    maxInd3 = j;
                }
                list.Add((i, j));
            }

            dp[i, 0] = maxInd1;
            dp[i, 1] = maxInd2;
            dp[i, 2] = maxInd3;
        }

        list = list.OrderBy(x => -board[x.Item1][x.Item2]).ToList();

        long result = Int64.MinValue;

        for (int i = 0; i < board.Length; i++){
            for (int j = i + 1; j < board.Length; j++){
                for (int ii = 0; ii < 3; ii++){
                    for (int jj = 0; jj < 3; jj++){
                        int f = dp[i, ii];
                        int s = dp[j, jj];
                        if (f == s){
                            continue;
                        }

                        var nextInd = list.First(x => x.Item1 != i && x.Item1 != j && x.Item2 != f && x.Item2 != s);
                        long newRes = (long)board[i][f] + board[j][s] + board[nextInd.Item1][nextInd.Item2];
                        result = Math.Max(result, newRes);
                    }
                }
            }
        }

        return result;
    }
}
