class Solution {
    using pii = pair<int, int>;

    vector<vector<pii>> buildRowTop3Largest(vector<vector<int>> &board) {
        int n = board.size(), m = board[0].size();
        // largest, second largest, third largest
        vector<vector<pii>> largest(n, vector<pii>(3));
        for(int i = 0; i < n; i++) {
            largest[i][0] = {board[i][0], 0};
            largest[i][1] = {board[i][1], 1};
            largest[i][2] = {board[i][2], 2};
            sort(largest[i].begin(), largest[i].end(), greater<>());

            for(int j = 3; j < m; j++) {
                if(board[i][j] > largest[i][0].first) {
                    largest[i][2] = largest[i][1];
                    largest[i][1] = largest[i][0];
                    largest[i][0] = {board[i][j], j};
                } else if(board[i][j] > largest[i][1].first) {
                    largest[i][2] = largest[i][1];
                    largest[i][1] = {board[i][j], j};
                } else if(board[i][j] > largest[i][2].first) {
                    largest[i][2] = {board[i][j], j};
                }
            }
        }
        return largest;
    }

    inline bool neq(int a, int b, int c) {
        return a != b && a != c && b != c;
    }

    long long getVal(vector<vector<pii>> &largest, int i, int j, int k, int a, int b, int c) {
        long long tmp = LLONG_MIN;
        if(neq(largest[i][a].second, largest[j][b].second, largest[k][c].second)) {
            tmp = (long long) largest[i][a].first + (long long) largest[j][b].first + (long long) largest[k][c].first;
        }
        return tmp;
    }

    void print(vector<vector<pii>>& largest) {
        for(int i = 0; i < largest.size(); i++) {
            cout << "row " << i << "'s largest:\n";
            for(int j = 0; j < largest[i].size(); j++) {
                cout << largest[i][j].first << "(" << largest[i][j].second << ") ";
            }
            cout << "\n";
        }
    }

public: // do not put them in the same row or col
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<pii>> largest = buildRowTop3Largest(board);
        // print(largest);

        long long ans = LLONG_MIN;
        vector<pii> prevLargest = largest[0];
        for(int i = 1; i < n; i++) {
            for(int j = i + 1; j < n; j++) {
                // 窮舉兩個 row 的組合
                for(int a = 0; a < 3; a++) {
                    for(int b = 0; b < 3; b++) {
                        if(largest[i][a].second == largest[j][b].second) continue;

                        for(int k = 0; k < 3; k++) {
                            if(neq(largest[i][a].second, largest[j][b].second, prevLargest[k].second)) {
                                ans = max(
                                    ans,
                                    (long long)largest[i][a].first + (long long)largest[j][b].first + (long long) prevLargest[k].first
                                );
                                break;
                            }
                        }
                    }
                }
            }

            vector<pii> temp = prevLargest;
            for(int a = 0; a < 3; a++) {
                temp.push_back(largest[i][a]);
            }
            sort(temp.begin(), temp.end(), greater<>());

            int ind = 0;
            for(int a = 0; a < temp.size() && ind < 3; a++) {
                bool valid = true;
                for(int b = 0; b < a; b++) {
                    if(temp[a].second == temp[b].second) {
                        valid = false;
                        break;
                    }
                }
                if(!valid) continue;

                prevLargest[ind] = temp[a];
                ind++;
            }
        }
        return ans;
    }
};
