class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();
        vector<vector<pair<int,int>>> l,r;
        vector<int> a(m,INT_MIN);
        for(int j=0;j<n;++j){
            vector<pair<int,int>> t(4,{INT_MIN,0});
            for(int i=0;i<m;++i){
                a[i]=max(a[i],board[i][j]);
                t[0]={a[i],i};
                sort(t.begin(),t.end());
            }
            l.push_back({});
            l.back().push_back(t[3]);
            l.back().push_back(t[2]);
            l.back().push_back(t[1]);
        }
        a=vector<int>(m,INT_MIN);
        for(int j=n-1;j>=0;--j){
            vector<pair<int,int>> t(4,{INT_MIN,0});
            for(int i=0;i<m;++i){
                a[i]=max(a[i],board[i][j]);
                t[0]={a[i],i};
                sort(t.begin(),t.end());
            }
            r.push_back({});
            r.back().push_back(t[3]);
            r.back().push_back(t[2]);
            r.back().push_back(t[1]);
        }
        long long res=LLONG_MIN;
        for(int j=1;j+1<n;++j){
            auto& ll=l[j-1];
            auto& rr=r[n-2-j];
            for(int i=0;i<m;++i){
                for(int il=0;il<3;++il){
                    for(int ir=0;ir<3;++ir){
                        if(i!=ll[il].second&&i!=rr[ir].second&&ll[il].second!=rr[ir].second){
                            long long tt=(long long)board[i][j]+ll[il].first+rr[ir].first;
                            // cout<<"tt="<<tt<<" i="<<i<<" j="<<j<<" ll="<<ll[il].second<<" rr="<<rr[ir].second<<endl;
                            res=max(res,tt);
                        }
                    }
                }
            }
        }
        return res;
    }
};
