class Solution {
public:
    using int64 = long long;
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n =board[0].size();
        vector<vector<pair<int, int>>> e(m, vector<pair<int, int>>(n));
        vector<vector<int>> f;
        for(int i = 0; i < m; ++i){
            for(int j = 0; j < n; ++j){
                e[i][j].first = board[i][j];
                e[i][j].second = j;
            }
            sort(e[i].begin(), e[i].end(), [&](const auto &x, const auto &y){
                return x.first > y.first;
            });
            for(int j = 0; j < 3; ++j){
                // f[i][j].push_back(e[i][j].first);
                // f[i][j].push_back(i);
                // f[i][j].push_back(e[i][j].second);
                f.push_back({e[i][j].first, i, e[i][j].second});
            }
        }
        int64 res = -1e12;
        sort(f.begin(), f.end(), [&](const auto &x, const auto &y){
            return x[0] > y[0] || (x[0] == y[0] && x[1] > y[1]);
        });
        int len = f.size();
        // cout << len << endl;
        for(int i = 0; i < len; ++i){
            for(int j = i + 1; j < i + 100 && j < len; ++j){
                if(f[i][1] != f[j][1] && f[i][2] != f[j][2]){
                    for(int k = j + 1; k < j + 100 && k < len; ++k){
                        if(f[i][1] != f[k][1] && f[i][2] != f[k][2] && f[k][1] != f[j][1] && f[k][2] != f[j][2]){
                            res = max(res, (int64)f[i][0] + f[j][0] + f[k][0]);
                        }
                    }
                }
            }
        }
        return res;
    }
};
