class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        total = []
        for i, row in enumerate(board):
            h = []
            for j, col in enumerate(row):
                if len(h) == 3:
                    _ = heappushpop(h, (col, i, j))
                else:
                    heappush(h, (col, i, j))

            for _ in range(3):
                heappush(total, h.pop())

        total.sort(reverse=True)
        ans = float('-inf')
        for i in range(len(total) - 2):
            cnt = 0
            for j in range(i + 1,  len(total) - 1):
                if total[j][1] == total[i][1] or total[j][2] == total[i][2]:
                    continue
                for k in range(j + 1, len(total)):
                    if total[k][1] == total[i][1] or total[k][1] == total[j][1] or total[k][2] == total[i][2] or total[k][2] == total[j][2]:
                        continue
                    ans = max(ans, total[i][0] + total[j][0] + total[k][0])
                    cnt += 1
                    break

                if cnt >= 3:
                    break


        return ans
