class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n=board.size();
        long long m=board[0].size();
        long long ans=-10000000000;
        vector< vector<pair<long long,long long>>>v(n+1);
        vector< vector<pair<long long,long long>>>v2(n+1);
        for(long long i=n-1;i>=0;i--){
            for(long long j=0;j<m;j++){
                v[i].push_back({board[i][j],j});
            }
            sort(v[i].rbegin(),v[i].rend());
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<3;j++){
                v2[i].push_back(v[i][j]);
            }
        }
        for(int i=0;i<n-2;i++){
            for(int x=0;x<3;x++){
                long long a1=v2[i][x].first;
                for(int j=i+1;j<n-1;j++){
                    for(int y=0;y<3;y++){
                        if(v2[i][x].second==v2[j][y].second){
                            continue;
                        }
                        long long a2=v2[j][y].first;
                        for(int k=j+1;k<n;k++){
                            for(int z=0;z<3;z++){
                                if(v2[k][z].second!=v2[i][x].second&&v2[k][z].second!=v2[j][y].second){
                                    long long max1=a1+a2+v2[k][z].first;
                                    if(ans<max1){
                                        ans=max1;
                                    }
                                    break;
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
