class Solution {
public:
    long long cal(int i, int j, vector<vector<pair<int,int>>>& all, vector<pair<int,int>>& allL){
            long long res = all[i][0].first;
            int t1 = all[i][0].second;
            if(all[j][0].second != t1){
                int t2 = all[j][0].second;
                res += all[j][0].first;
                int t = 0;
                while(true){
                    int r = allL[t].second/500;
                    int c = allL[t].second%500;
                    if(r != i && r != j && c != t1 && c != t2){
                        res += allL[t].first;
                        break;
                    }
                    t++;
                }
            }
            else{
                int t2 = all[j][1].second;
                res += all[j][1].first;
                int t = 0;
                while(true){
                    int r = allL[t].second/500;
                    int c = allL[t].second%500;
                    if(r != i && r != j && c != t1 && c != t2){
                        res += allL[t].first;
                        break;
                    }
                    t++;
                }
            }
            return res;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<pair<int,int>> allL;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                allL.push_back({board[i][j], i*500 + j});
            }
        }
        sort(allL.begin(), allL.end(), greater());
        vector<vector<pair<int,int>>> all(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                all[i].push_back({board[i][j], j});
            }
        }
        for(int i=0;i<n;i++){
            nth_element(all[i].begin(), all[i].begin() + 3, all[i].end(), greater());
            sort(all[i].begin(), all[i].begin() + 3, greater());
        }
        long long res = LLONG_MIN;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                res = max({res, cal(i,j,all,allL), cal(j,i,all,allL)});
            }
        }
        return res;
    }
};
